  const [showSearch, setShowSearch] = useState(false);
  const [changeStyle1, setChangeStyle1] = useState(true);
  const [changeStyle2, setChangeStyle2] = useState(false);

  const handleClickManualEntry = () => {
    setShowManual(true);
